"-DCMAKE_DISABLE_FIND_PACKAGE_Neo=ON " \
)
lenList=${#pkgsList[@]}
endList=""

for (( counter=0; counter<10; counter++ ))
do
  alea=$(( $RANDOM % $lenList ))
  elem=${pkgsList[$alea]}
  endList+=$elem
  pkgsList=("${pkgsList[@]/$elem}")
  lenList=$lenList-1
done
echo $endList